package ch04;

public class MyThread extends Thread {
  public static boolean ready = false; // 「開跑」變數

  // 以四種不同的建構者函數來建立物件 (function overloading)
  public MyThread() {
    super("NT1");
  }

  public MyThread(String name) {
    super(name);
  }

  public MyThread(String name, int priority) {
    super(name);
